// Read a single pixel from the pattern buffer
float getPatternPixel(int whichPattern, float coordX, float coordY) { 
    float coordStart = floor(float(whichPattern * WIDTH * HEIGHT));
    float coordReal = floor(coordStart + coordY * float(WIDTH) + coordX);

    float coordYReal = (floor(coordReal / iResolution.x) + 0.5) / iResolution.y;
    float coordXReal = (floor(mod(coordReal, iResolution.x)) + 0.5) / iResolution.x;

    return(texture(iChannel1, vec2(coordXReal, coordYReal)).r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    int showPattern = 0;

	vec2 uvOrig = (floor(fragCoord.xy) / iResolution.xy);
    float coordY = floor(uvOrig.y * float(HEIGHT));
    float coordX = floor(uvOrig.x * float(WIDTH));

    fragColor = vec4(0.1, 0.1, 0.1, 1.0); //vec4(getPatternPixel(showPattern, coordX, coordY));
    if (getPatternPixel(showPattern, coordX, coordY) == -1.0) {
		fragColor = vec4(0.9, 0.9, 0.9, 1.0);
    }

    if (getPatternPixel(showPattern, coordX, coordY) == 1.0) {
		fragColor = vec4(0.1, 0.1, 0.1, 1.0);
    }

    if(uvOrig.y > 0.98) {
        if(uvOrig.x * 15.0 < mod(iTime, 20.0) && mod(iTime, 20.0) < 15.0) {
    		fragColor = vec4(1.0, 0.0, 0.0, 1.0);
        }
    }
    //fragColor = vec4(getPatternPixel(showPattern, coordX, coordY));
}

// Read a single pixel from the pattern buffer
float getPatternPixel(int whichPattern, float coordX, float coordY) { 
    float coordStart = floor(float(whichPattern * WIDTH * HEIGHT));
    float coordReal = floor(coordStart + coordY * float(WIDTH) + coordX);

    float coordYReal = (floor(coordReal / iResolution.x) + 0.5) / iResolution.y;
    float coordXReal = (floor(mod(coordReal, iResolution.x)) + 0.5) / iResolution.x;

    return(texture(iChannel1, vec2(coordXReal, coordYReal)).r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec4 outColor = vec4(0.0);
    vec2 fcProper = vec2(int(fragCoord.x), int(fragCoord.y));

    // Update all four colour components with hebbian-rule weights
    if(iTime < 0.5) {
        for(int component = 0; component < 4; component++) {
            int idx = int(fcProper.y * iResolution.x + fcProper.x) * 4 + component;
            int thisNeuron = idx / (WIDTH * HEIGHT);
            int incomingNeuron = idx % (WIDTH * HEIGHT);

            int thisX = thisNeuron % WIDTH;
            int thisY = thisNeuron / WIDTH;

            int incX = incomingNeuron % WIDTH;
            int incY = incomingNeuron / WIDTH;

            float weight = 0.0;
            for(int pattern = 1; pattern <= PATTERNS; pattern++) {
                weight += getPatternPixel(pattern, float(thisX), float(thisY)) * 
                          getPatternPixel(pattern, float(incX), float(incY));
            }

            // Cool Hack(tm): since this is a hopfield net, we can wire it to retrieve
            // one pattern if another is specified (this will oscillate wildly)
            /*weight += getPatternPixel(1, float(thisX), float(thisY)) * 
                      getPatternPixel(2, float(incX), float(incY));
            weight += getPatternPixel(2, float(thisX), float(thisY)) * 
                      getPatternPixel(1, float(incX), float(incY));
			*/

            outColor[component] = (weight / float(PATTERNS)) * 0.1;
        }
		fragColor = outColor;
    }
    else {
        fragColor = texture(iChannel0, fragCoord.xy / iResolution.xy);
    }
}

// Read a single pixel from the pattern buffer
float getPatternPixel(int whichPattern, float coordX, float coordY) { 
    float coordStart = floor(float(whichPattern * WIDTH * HEIGHT));
    float coordReal = floor(coordStart + coordY * float(WIDTH) + coordX);

    float coordYReal = (floor(coordReal / iResolution.x) + 0.5) / iResolution.y;
    float coordXReal = (floor(mod(coordReal, iResolution.x)) + 0.5) / iResolution.x;

    return(texture(iChannel1, vec2(coordXReal, coordYReal)).r);
}

// Read weight for connection going from neuron incomingNeuron into thisNeuron
float readWeight(int thisNeuron, int incomingNeuron) {
    int idx = WEIGHT_IDX(thisNeuron, incomingNeuron);

    int component = idx % 4;
    idx = idx / 4;

    int row = idx % int(iResolution.x);
    idx = idx / int(iResolution.x);

    int col = idx;

	return texelFetch(iChannel0, ivec2(row, col), 0)[component];
}

vec3 hash33(vec3 p){ 
    float n = sin(dot(p, vec3(7, 157, 113)));    
    return fract(vec3(2097152, 262144, 32768)*n); 
}

// Generate patterns or, for pattern 0, evaluate.
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    int coord = int(int(fragCoord.y) * int(iResolution.x) + int(fragCoord.x));

    float pattern = float(coord / (WIDTH * HEIGHT));
    coord = coord % (WIDTH * HEIGHT);

    float y = float(coord / WIDTH);
    float x = float(coord % WIDTH);

    vec2 mouseCoords = (iMouse.xy / iResolution.xy) * vec2(WIDTH, HEIGHT);

    float outColor = 1.0;
    vec2 coords = vec2(x - float(WIDTH / 2), y - float(HEIGHT / 2));
    if(iTime < 1.5) {
        // Pattern generation
        if(int(pattern) == 1) {
            if(abs(coords.x - coords.y) <= 1.0 || abs(coords.x + coords.y) <= 1.0) {
                if(length(coords) < 15.0) {
                    outColor = -1.0;
                }
            }
        }

        if(int(pattern) == 2) {
            if(length(coords) < 10.0 && length(coords) > 7.5) {
                outColor = -1.0;   
            }
        }

        if(int(pattern) == 3) {
            if(length(coords) < 7.5 && length(coords) > 4.5) {
                outColor = -1.0;   
            }
        }

        /*if(int(pattern) == 2) {
            if(coords.x > -12.0 && coords.x < -9.0) {
                if(coords.y > -12.0 && coords.y < 12.0) {
                    outColor = -1.0;
                }
            }

            if(coords.y > -12.0 && coords.y < -8.0) {
                if(coords.x > -12.0 && coords.x < 12.0) {
                    outColor = -1.0;
                }
            }
        }*/
    }

    // Net evaluation
    if(int(pattern) == 0) {
        // Init
        if(mod(iTime, 20.0) < 0.5) {
            // Try to compensate for biases a little. Might cause net to hit spurious patterns.
            outColor = texture(iChannel2, vec2(x, y) / vec2(WIDTH, HEIGHT)).r > 0.65 ? -0.99 : 0.99;
        }
        else {
         	outColor = getPatternPixel(0, float(x), float(y));
        }
        // Allow draw
        if(mod(iTime, 20.0) < 15.0) {
            if(length(vec2(x, y) - mouseCoords) < 0.5) {
                outColor = -1.0;
            }
        }

        // Evaluate
        if(mod(iTime, 20.0) >= 15.0) {
            float neuronUpdate = 0.0;
            if(hash33(vec3(iTime, x, y)).r > NO_UPDATE_LIKELIHOOD) {
                int thisNeuron = int(y) * WIDTH + int(x);
                for(int incX = 0; incX < WIDTH; incX++) {
                    for(int incY = 0; incY < HEIGHT; incY++) {
                        int incomingNeuron = incY * WIDTH + incX;

                        float netState = getPatternPixel(0, float(incX), float(incY));
                        float incWeight = readWeight(thisNeuron, incomingNeuron);

                        neuronUpdate += netState * incWeight;
                    }
                }

                outColor = 1.0;
                if(neuronUpdate < 0.0) {
                    outColor = -1.0;
                }
            }
            else {
               outColor = getPatternPixel(0, float(x), float(y));
            }

        }
    }

    fragColor = vec4(outColor);
}
