#define PI 3.14159265
#define D(a,b) 1.-abs(2.*(a-b.x)/(b.y-b.x)-1.)
#define S(op,a,b) l=vec2(op(uv.x+iTime*a))+vec2(-eps,eps);if(uv.y>l.x&&uv.y<l.y)b+=D(uv.y, l);
vec2 R=iResolution.xy;
float s=sin(iTime),eps =.1+.25*s*s;

void mainImage(out vec4 C,in vec2 U)
{
	vec2 uv=3.*U/R-1.5,l;
    uv.x*=2.*R.x/R.y;
    uv*=pow(5./length(uv),2.);
    vec3 c=vec3(0);
    S(sin,.25,c.r)
    S(sin,.5,c.g)
    S(sin,.75,c.b)
    S(cos,.25,c.rg)
    S(cos,.5,c.rb)
    S(cos,.75,c.gb)
    C.xyz= min(c,1.);
}
