#define PulseDuration 1.

float GetBias(float x,float bias)
{
  return (x / ((((1.0/bias) - 2.0)*(1.0 - x))+1.0));
}

float GetGain(float x,float gain)
{
  if(x < 0.5)
    return GetBias(x * 2.0,gain)/2.0;
  else
    return GetBias(x * 2.0 - 1.0,1.0 - gain)/2.0 + 0.5;
}

#define PIKE .15
float progToCurv(float f)
{
	if(f < PIKE)
    {
    	return GetGain(f/PIKE,.25);
    }
    else
    {
        return 1.-GetBias((f - PIKE) / (1.-PIKE),.75);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	float ct = texture(iChannel0,vec2(.5)).x;

    float f = clamp(iTime - ct,0.,PulseDuration);
    f = f/ PulseDuration;
    f = progToCurv(f);

    float d  = -distance(uv,iMouse.xy / iResolution.xy)+.5;
    d = GetGain(d,.05);
    f = f * d ;
    float r = texture(iChannel1,uv + vec2( .0  , .707) * f).r;
    float g = texture(iChannel1,uv + vec2( .707,-.707) * f).g;
    float b  =texture(iChannel1,uv + vec2(-.707,-.707) * f).b;

    fragColor = vec4(r,g,b,1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	float f = texture(iChannel0,vec2(.5)).x;
    if(iMouse.z > .5)
    	f = iTime;
    fragColor = vec4(f);
}
