#define PI 3.14159265359

float plot(vec2 st, float pct){
  return  smoothstep( pct-0.05, pct, st.y) - 
          smoothstep( pct, pct+0.05, st.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 st = fragCoord.xy/iResolution.xy;

    vec3 color = vec3(st.y * sin(iTime));

    float v = (cos((st.x * 10.0) + iTime * 1.0) + cos((st.x * 10.0)*2.0/3.0*3.1415 + iTime * 1.0) )/5.0 + 0.5;
    float v2 = v +  sin((st.x * 20.0)*50.0*3.1415 + iTime * 1.0) / 10.0  ;
    float v3 = (cos((st.x*sin(st.y) * 10.0) + iTime * 1.0) + cos((st.x * 10.0)*2.0/3.0*3.1415 + iTime * 1.0) )/5.0 + 0.5;

    float pct = plot(st ,v2);


    color = (pct)*color+pct*vec3(1.0,0.0,0.0);

	fragColor = vec4(color,1.0);
}
