vec4 color(float r, float g, float b)
{
    return vec4(r/255.0, g/255.0, b/255.0, 0.1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.yy;
	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
	fragColor = vec4(1.0);

    float circle1 = length(0.5 - uv + vec2((iResolution.y / iResolution.x), 0.0) / 1.7);
    if(circle1 < 0.5)
        fragColor = vec4(0.1);

    if(circle1 / sin(iTime) < 0.6)
        fragColor = color(255.0, 25.5, 127.5);

    if(sin(iTime) < circle1 / 2.0)
		fragColor = color(100.0, 200.00, 255.0);

    //if(tan(iTime) < circle1)
      //  fragColor = color(155.0, 3.0, 6.0);

}
