void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec4 chromaKeyColor = vec4(0.05, 0.63, 0.14, 1.0);

	vec2 uv = fragCoord.xy / iResolution.xy;
    float m = 0.5 - uv.x;

    float woblyCoef = sin(iTime * 0.1) * 0.1; 

    uv.x += sin(uv.y * (cos(iTime*0.01) * 10.0 + 3.0) + iTime*10.0) * woblyCoef;
    //uv.y += cos(uv.x * 50.0 + iTime*10.0)/100.0;


    vec4 col = texture(iChannel0, uv);

    float chromaKeyDiff = length(col - chromaKeyColor);   
    chromaKeyDiff = clamp(chromaKeyDiff * 1.25, 0.0, 1.0);

	fragColor = col * chromaKeyDiff;
}
