bool circle (vec2 uv, vec2 p, float radius)
{
    	float dist = sqrt((uv.x - p.x) * (uv.x -p.x) + (uv.y - p.y) * (uv.y - p.y)); 
    	if (dist < radius) 
            return true;
    	else return false; 


}


vec4 heart(vec2 p, vec2 cen, float rad) { 

    vec2 relative = p-cen; //relative position
    float a = atan(relative.x, relative.y)/3.141593; //angle 
    float r = a*rad; //radius 
    float t;
    float step = 1.0; 

    if(relative.x > 0.0){
        //right area 

        float d = length(p-cen)-r*1.5; 
        t = smoothstep (0.5, step, d); 

    }else{ 
        //left area

        float d = length(p-cen)-r*-1.5;
        t = smoothstep (0.0, step, d);

    } 

    return vec4(0.9,0.3,0.5,1.0-t);    //color

}


void mainImage (out vec4 fragColor, in vec2 fragCoord)
{    
    vec2 uv = fragCoord.xy / iResolution.xy;

    uv -= .5;				//-0.5 < > 0.5
	uv.x *= iResolution.x/iResolution.y; 

    vec2 center = vec2(0., 0.);

 //heart 


    vec3 col = vec3(0.); 
    vec2 center1 = vec2(0.5, 0.8); 

  	vec4 heartColor = heart(fragCoord, center1.xy*iResolution.xy, 200.);
    vec4 temp = vec4(col,1.0); 
    fragColor = mix(temp, heartColor,heartColor.a);



 //green circle 

    float radiusGreen = 0.22;

    bool isInGreenCircle = circle(uv,center, radiusGreen); 
    if (isInGreenCircle) fragColor = vec4 (0, 1, 0, 1); 

//yellow circle

    float radius = 0.2;


	bool isInCircle = circle(uv, center, radius); 
	if (isInCircle) fragColor = vec4(1,1,0,1);

// Mouth Draw 

   vec2 mouthCenter1 = vec2(0., -0.09); 
   vec2 mouthCenter2 = vec2(0.,-0.06); 

   float mouthRadius1 = 0.07 * (1.0 + sin(iTime)) * 0.4;
   float mouthRadius2 = 0.06 * (1.0 + sin(iTime)) * 0.4;

   bool isInMouth1 = circle(uv, mouthCenter1, mouthRadius1);
   if (isInMouth1) fragColor = vec4(0, 1, 6, 3);

   bool isInMouth2 = circle(uv, mouthCenter2, mouthRadius2);
   if (isInMouth2)fragColor = vec4(1, 1, 0, 1);

// Eyes Draw

    vec2 leftEye1 = vec2 (-0.09, 0.06);
    vec2 rightEye2 = vec2 (0.09, 0.06);

	float leftEyeRadius1 = 0.1 * (1.0 + sin(iTime)) * 0.9;
    float rightEyeRadius2 = 0.1 * (1.0 + sin(iTime)) * 0.9;

    bool isInlEye = circle (uv, leftEye1, leftEyeRadius1);
    if (isInlEye) fragColor = vec4 (sin(iTime),0,0,1);

	bool isInrEye = circle (uv, rightEye2, rightEyeRadius2);
    if  (isInrEye) fragColor = vec4 (sin(iTime),0,0,1); 

 //heart


}
