float random (in float x) 
{
    return fract(sin(x) * 1e5);
}

float random (vec2 st) 
{
    return fract(sin(dot(st.xy, vec2(5.9898, 78.233))) * 43758.5453123);
}

float randomSerie(float y, float freq, float t) 
{
    return step(.8,random( floor(y * freq) - floor(t)));
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy / iResolution.xy;


    st *= 150.0;
    vec2 ipos = floor(st);
    vec2 fpos = fract(st);

    float freq = random(floor(iTime)) + abs(sin(iTime * 0.52) * 0.5);
    float t = 60. + iTime * (1.0 - freq);

    if (fract(st.y * 0.5) < 0.5)
    {
        t *= 10000.0;
    }

    freq += random(floor(st.x));

    float offset = 0.5;
    vec3 color = vec3(randomSerie(st.y, freq, 0. ),
                 randomSerie(st.y, freq, t),
                 randomSerie(st.y, freq, t - offset));


    fragColor = vec4(color, 1.0);
}
