#define PIXEL_SNAP 0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
#if PIXEL_SNAP
    fragCoord-=mod(fragCoord-iResolution.xy*.5,floor(pow(2.,iTime*.2)));
#else
    fragCoord-=mod(fragCoord-iResolution.xy*.5,pow(2.,iTime*.2));
#endif

    vec2 uv = fragCoord/iResolution.xy-.5;
    uv.x*=iResolution.x/iResolution.y;
    fragColor = texture(iChannel0,uv);
}
