void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 v = fragCoord / iResolution.xy;

    float a = v.x * 6.28318 + iTime; 
	float b = v.y * 3.14159 - 1.57079; 

	vec3 rd = vec3(
        cos(b) * cos(a),
    	sin(b),
    	cos(b) * sin(a));

    fragColor = texture(iChannel0, rd);
}
