#define BG_COLOR vec3(.9)
#define FG_COLOR vec3(.8)
#define ST_COLOR vec3(.1)

float PointSegDistance(vec2 p, vec2 a, vec2 b)
{
	vec2 pa = p-a, ba = b-a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h );
}

float speed(int n)
{
    return mapSpeed(texelFetch(iChannel0, ivec2(n, 0),0).r);
}

float quantity(int n)
{
    return mapQuantity(texelFetch(iChannel1, ivec2(n, 0),0).r * .5);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    int x = int(fragCoord.x);
    float dx = 1./iResolution.x;

    float v = speed(x);
    vec2 p  = vec2(uv.x   , v       );
    vec2 pp = vec2(uv.x+dx, speed(x+1));
    vec2 pm = vec2(uv.x-dx, speed(x-1));

    if(x == 0)
        pm = pp;
    if(x == int(iResolution.x)-1)
        pp = pm;

    float dp = min(PointSegDistance(uv,p, pp),PointSegDistance(uv,p, pm));

    float stroke_alpha = clamp(mix(1., 0., dp * iResolution.y), 0., 1.);
    float bg_alpha = clamp((v-uv.y)* iResolution.y, -1., 1.)*.5+.5;
    bg_alpha *= clamp((uv.y-0.75)* iResolution.y, 0., 1.);
    float bg_alpha_2 = clamp((-v+uv.y)* iResolution.y, -1., 1.)*.5+.5;
    bg_alpha_2 *= clamp(-(uv.y-0.75)* iResolution.y, 0., 1.);

    bg_alpha += bg_alpha_2;

    vec3 c = mix(BG_COLOR, FG_COLOR, bg_alpha);
    c = mix(c, ST_COLOR, stroke_alpha);


    v = quantity(x);
    p  = vec2(uv.x   , v       );
    pp = vec2(uv.x+dx, quantity(x+1));
    pm = vec2(uv.x-dx, quantity(x-1));

    if(x == 0)
        pm = pp;
    if(x == int(iResolution.x)-1)
        pp = pm;

    dp = min(PointSegDistance(uv,p, pp),PointSegDistance(uv,p, pm));

    stroke_alpha = clamp(mix(1., 0., dp * iResolution.y), 0., 1.);
    bg_alpha = clamp((v-uv.y)* iResolution.y, -1., 1.)*.5+.5;

    c = mix(c, FG_COLOR, bg_alpha);
    c = mix(c, ST_COLOR, stroke_alpha);


    c = mix(vec3(0.), c, clamp(abs(uv.y-.5)*iResolution.y - 1., 0., 1.));


    fragColor = vec4(c, 1.0);
}

