void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 ifragCoord = ivec2(floor(fragCoord));

    if(ifragCoord.y != 0)
    {
        fragColor = vec4(0.);
        return;
    }



    if(iFrame <2)
    {
        float x = fragCoord.x/iResolution.x;
        float val = .5*sin(x*30.);
        fragColor = vec4(val, 0., 0., 0.);
        return;
    }

    float val = texelFetch(iChannel0, ifragCoord, 0).r;
    //float val = texture(iChannel0, (fragCoord - RK4(fragCoord) * dt)/iResolution.x).x;


    if(iMouse.y/iResolution.y > .5 && iMouse.z > .5 && abs(iMouse.x-fragCoord.x)/iResolution.x < brushRadius)
        val += dt * brushIntensity * smoothstep(0.,1.,1.-abs(iMouse.x-fragCoord.x)/iResolution.x / brushRadius)*sign(iMouse.y/iResolution.y - .75);



    fragColor.x = val;

}

float RK1(float p)
{
    return texture(iChannel0,vec2(p/iResolution.x, 0.)).x;
}

float RK4(float p)
{
    float k1 = RK1(p);
    float k2 = RK1(p-0.5*dt*k1);
    float k3 = RK1(p-0.5*dt*k2);
    float k4 = RK1(p-dt*k3);
    return (0.5*k1+k2+k3+0.5*k4)/3.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    ivec2 ifragCoord = ivec2(floor(fragCoord));

    if(ifragCoord.y != 0)
    {
        fragColor = vec4(0.);
        return;
    }

    if(iFrame == 0)
    {
        float x = fragCoord.x/iResolution.x;
        float val = abs(sin(x*35.)*sin(x*10.));
        fragColor = vec4(val, 0., 0., 0.);
        return;
    }

    float val = texture(iChannel1, (fragCoord - vec2(RK4(fragCoord.x), 0.) * dt)/iResolution.x).x;

    if(iMouse.y/iResolution.y < .5 && iMouse.z > .5 && abs(iMouse.x-fragCoord.x)/iResolution.x < brushRadius)
        val += dt * brushIntensity * smoothstep(0.,1.,1.-abs(iMouse.x-fragCoord.x)/iResolution.x / brushRadius);

    fragColor = vec4(val, 0., 0., 0.);
}

float brushRadius = .02;
float brushIntensity = .01;
float dt = 2.;

float mapSpeed(float speed)
{
    return speed*.25+.75;
}

float unMapSpeed(float speed)
{
    return 4.*(speed-.75);
}

float mapQuantity(float q)
{
    return q*.5;
}

float unMapQuantity(float q)
{
    return q*2.;
}
