void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Variables
    float col = 15.0;
    float thickness = 1.0;
    float antialiasing = 0.001;
    float intensity = 1.0;
    float speed = 0.9;
    vec2 offset = vec2(1.0,1.0);
   	vec4 color = vec4(0.0,0.7,1.0,1.0);

    vec2 uv = (fragCoord.xy/iResolution.x)*col;

    float u = (fragCoord.x/iResolution.x)*col;

    float grepU = abs(fract(u) - 0.5) * thickness;

    color *= smoothstep(0.4, 0.5, grepU);
    fragColor = vec4(color*intensity);
    fragColor.y *= 1.2;
    fragColor.z *= 1.8;
}
