void mainImage( out vec4 O, in vec2 U ){
	vec2 uv = U.xy / iResolution.y, mouse = iMouse.xy / iResolution.xy;
    float time = iTime *  mouse.x * 7.,  level = mouse.y * 2. -.5;

    level += cos(tan(sin(time*0.33)))*0.5-0.3;
    time+=sin(time*0.2)*0.3;
    vec3 blur    = vec3(.1,      .3,     .2) * 3.1;   
  //  if(uv.x<0.3) blur *= 0.0;
   vec3  speed    = vec3(-.7,    -.14,    2.12),
     amplitude  = vec3(.223,  .21,   .24),
     frequency  = vec3(2.12,   4.6,   7.5) * .2,
     wave = vec3( sin(uv.x * frequency - time * speed) * amplitude + level );

    vec3 cover = vec3(uv.y*0.5);
    vec3 final = smoothstep(wave, wave + blur, uv.yyy);
    if(uv.x > 0.7){
        final += cover;
    }
    O = vec4(final,1.0);
}
