void mainImage( out vec4 o, vec2 p ) {
    vec2 R = iResolution.xy;
    float t = iTime;

    for(int x=0; x<20; x++) 
    	for(int y=0; y<20; y++)
        {
            vec4 S = 1. + sin(t*vec4(2,3,5,11));
            length( p - vec2(x,y)*R/20. ) / R.y < S.w/66.  ?  o = S/2. : o ;                       
            t += .1;
        }
}
