float round(float a) {
    return a>.5?ceil(a):floor(a);
}
vec3 Bricks(vec2 uv) {

    vec3 red = vec3(1,0,0);
    vec3 white = vec3(1);
    float i = 0.;
    float a = round(mod(mod(uv.x,10.)/3.,3.));
    float b = round(mod(mod(uv.y,6.)/2.,3.));
    if (a==0.||b==0.) i = 0.; else i = 1.;
    return mix(red,white,1.-(i));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = vec4(Bricks((uv+vec2(-.5,.5+iDate.w))/(.5-uv.y)*50.)*abs(.5-uv.y),1.0);
}
