// line 34: pixel coordinates	
// line 14: c travels around the main cardiod c(t) = Â½e^it - Â¼e^i2t
// line 25: z = zÂ² + c		
// line 26: trap orbit
// line 45: remap	
// line 46: color	
// line 47: split screen

vec2 offset( float t )
{
    return 0.51*cos( vec2(0.0,1.5708) + 0.1*t )
         - 0.25*cos( vec2(0.0,1.5708) + 0.2*t );
}

float orbit( vec2 z, vec2 bias )
{
	float f = 1e20;
	for( int i=0; i<128; i++ ) 
	{
		z = vec2( z.x*z.x-z.y*z.y, 2.0*z.x*z.y ) + bias;
		f = min( f, dot(z,z) );
	}
    return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float ar = 4./3.; // aspect ratio
	vec2 p = ar*(-iResolution.xy+2.0*fragCoord.xy)/iResolution.y;
	vec2 m = ar*(-iResolution.xy+2.0*iMouse.xy   )/iResolution.y;

	float f;

    // Right Half = Static, Time=0
    if( p.x > m.x )
        f = orbit( p, offset( 0.0 ) );
    else
        f = orbit( p, offset( iTime ) );

    f = 1.0+log(f)/16.0;
    vec3 color = vec3( f, f*f, f*f*f );
    color *= smoothstep( 0.006, 0.008, abs(p.x-m.x) );

	fragColor = vec4(color,1.0);
}
