void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    const int iterations = 200;
    float x = -1.75 + (3.5 / iResolution.x) * fragCoord.x;
    float y = -1.5 + (3.0 / iResolution.y) * fragCoord.y;
    float real = (iMouse.x / iResolution.x) * 2.0 - 1.0;
	float imaginary = (iMouse.y / iResolution.y) * 2.0 - 1.0;

    int count = 0;
    for (int i = 0; i < iterations; i++) {
		if (x*x + y*y > 4.0) break;
		count = i;

		float new_x = x*x - y*y + real;
		float new_y = 2.0*x*y + imaginary;
		x = new_x;
		y = new_y;	
	}

    float alk = float(count) / (float(iterations) / 5.0);

    fragColor = vec4(0, 0, alk, 1);

}
