void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.97, 0.97, 0.97);
    float thickness = 0.01;
    float count = 30.0;
    vec2 modCoord = mod(fragCoord/iResolution.xy + vec2(0,-0.5), vec2(iResolution.x,1.0/count));
    fragCoord = fragCoord/iResolution.xy;


    vec4 t = texture(iChannel0, vec2(fragCoord.x, floor((fragCoord.y)*float(count))/float(count)));
    float avg = (t.x + t.y + t.z)/3.0;

    float e = 1.0/iResolution.y;
    col *= smoothstep(-e, e, abs(modCoord.y - avg/count) - thickness);

    fragColor = vec4(col,1);
}
