// Book of Shaders
float rand(float n) {
    return fract(sin(n) * 43758.5453123);
}

// Book of Shaders
float noise(float p) {
	float fl = floor(p);
    float fc = fract(p);

	return mix(rand(fl), rand(fl + 1.0), fc);
}


float noiseyChannel(vec4 base, vec2 uv, int channel) {
    float distance = 0.05;

    uv.x += mix(-distance, distance, noise(iTime + ((float(channel) + 1.0) * 5.0)));
    uv.y += mix(-distance, distance, noise(iTime + ((float(channel) + 1.0) * 7.0)));
    return mix(base[channel], texture(iChannel0, uv)[channel], 0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 color = texture(iChannel0, uv);

    color.r = noiseyChannel(color, uv, 0);
    color.g = noiseyChannel(color, uv, 1);
    color.b = noiseyChannel(color, uv, 2);

    // Output to screen
    fragColor = color;
}
