#define N 1.  // grid step. 1 = continuous

void mainImage( out vec4 O, vec2 U )
{
    //O = .5 + .5* texelFetch(iChannel0,ivec2(U),0)/L; return;

    O -= O;
    for (float j=-L; j<=L; j++)
        for (float i=-L; i<=L; i++) {
            vec2 P = U+vec2(i,j), D, I = mod(P-.5,N);
            if (I.x+I.y==0.) {
                D = P+texelFetch(iChannel0,ivec2(P),0).xy - U;
                O += smoothstep(1.5,0.,length(D));
            }
        }
    O *= min(1., 16.*N*N/(L*L)) * vec4(1,.8,.6,1);
}

void mainImage( out vec4 O, vec2 U )
{
    O = texelFetch(iChannel0,ivec2(U),0); // offset to grid center ( max: L )

    float t = iTime/3.;
 	vec2  R = iResolution.xy,
          M = iMouse.xy, D;
    if ( length(M) < 10. ) 
        M = R/2. + R/2.5* vec2( cos(1.+t+.3*floor(10.*cos(4.7*t))/10.)+.3*sin(2.7*t), floor(3.*sin(1.31*t+.3*cos(5.1*t)))/3. +.2*cos(1.+3.7*t) );
    D = M - (U+O.xy);
    float l = length(D); 
    O.xy -= 3.*exp(-l/L)*D/l;  // -: repeal  +: attract

    l = length(O.xy); if (l>=L) O.xy *= L/l;
}
