float randomFloat(vec3 seed) {
    return fract(seed.z * 0.001f * seed.x * 11527.0f - 0.001f * seed.y * seed.z * 10039.0f + seed.z * 10607.0f);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(vec3(randomFloat(vec3(fragCoord / iResolution.xy, iTime))), 1);
}
