bool pointInRect(vec4 rect , vec2 p){

    return rect.x <= p.x && rect.y <= p.y && rect.z >= p.x && rect.w >= p.y;
}

vec4 getColor(vec4 rect1, vec4 color1, vec4 rect2, vec4 color2, vec2 point){
    vec4 c = vec4(0, 0, 0, 0);

    if(pointInRect(rect2, point)){
        c = color2;
    }

    if(pointInRect(rect1, point)){
        c = color1;
    }

    if(pointInRect(rect1, point) && pointInRect(rect2, point)){
        c = mix(color1, color2, color2.w);
    }
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 bgColor = vec4(1.0, 1.0, 1.0, 1);
    vec4 bg = vec4(0, 0, iResolution.x, iResolution.y);

    vec4 c1 = vec4(1.0, 0, 0, 1.0);
    vec4 c2 = vec4(0.0, 1.0, 0, 0.6);
    vec4 c3 = vec4(0.0, 0.0, 1.0, 0.6);

    float w0 = 200.0;
    float h0 = 200.0;
    float x0 = iMouse.x-w0/2.0;//-fragCoord.x;
    float y0 = iMouse.y-h0/2.0;//-fragCoord.y;


    vec4 rect1 = vec4(x0, y0, x0+w0, y0+w0);
    vec4 rect2 = vec4(250, 250, 450, 450);
    vec4 rect3 = vec4(375, 350, 600, 575);


    vec4 c = bgColor;
    c = getColor(bg, c, rect1, c1, fragCoord);
    c = getColor(bg, c, rect2, c2, fragCoord);
    c = getColor(bg, c, rect3, c3, fragCoord);
    fragColor = c;
}
