#define SIZE 19.6
#define ANIM_SPEED .125

//https://www.shadertoy.com/view/4djSRW
float hash12(vec2 p){
	vec3 p3=fract(p.xyx*.1031);
	p3+=dot(p3, p3.yzx+33.33);
	return fract((p3.x+p3.y)*p3.z);
}

//https://www.shadertoy.com/view/4dS3Wd
float noise(vec2 st){
	vec2 i=floor(st);
	vec2 f=st-i;

	float a=hash12(i);
	float b=hash12(i+vec2(1, 0));
	float c=hash12(i+vec2(0, 1));
	float d=hash12(i+vec2(1));
	vec2 u=f*f*(3.-2.*f);

	return mix(a, b, u.x)+
			(c-a)*u.y*(1.-u.x)+
			(d-b)*u.x*u.y;
}

#define OCTAVES 4
float fbm(vec2 st){
	float value=0.;
	float amplitude=.5;
	float frequency=0.;
	for(int i=0;i<OCTAVES;i++){
		value+=amplitude*noise(st);
		st*=2.;
		amplitude*=.5;
	}
	return value;
}

const float Pi=3.1415927;
void mainImage(out vec4 fragColor, in vec2 fragCoord){
	vec2 coord=fragCoord-.5*iResolution.xy;
	vec2 uv=fragCoord/iResolution.xy;
	float time=4.*ANIM_SPEED*round(iTime/ANIM_SPEED);

	//base blue
	fragColor=vec4(.07, .3, .7, 1.);
	fragColor+=.1-.2*fbm(8.*uv);

	//wiggle
	float angle=2.*Pi*fbm(uv*12.+time);
	float disp=.4+.5*fbm(time-4.*uv);
	coord+=disp*vec2(cos(angle), sin(angle));

	//white lines
	vec2 lines=abs(.5-fract(coord/SIZE));
	float val=smoothstep(.44, .5, max(lines.x, lines.y)+.015*hash12(coord));
	//every fifth line is brighter
	lines=abs(.5-fract(.2*coord/SIZE));
	val+=.67*smoothstep(.48, .5, max(lines.x, lines.y)+.005*hash12(coord));
	fragColor=mix(fragColor, vec4(.68, .7, 1, 1), val);

	//composite
    vec4 col=texture(iChannel0, uv);
    fragColor=mix(fragColor, col, smoothstep(.5, .6, length(col.rgb-vec3(.1, .9, .2))));

	//vignette
	uv*=1.-uv.yx;
	fragColor*=pow(24.*uv.x*uv.y, .19);
}
