// -10 by coyote, -2 by Fab

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy,
          U = ( u+u - R ) / R.y + vec2(1.7,.9), V, i;

    for( O *= 0.; i.x++ < 14.                                                     // depth
        ; U.x--, U *= mat2(cos(1.047*(.5+.5*sin(iTime)) +vec4(0,11,33,0))) * 1.33 // recursion
        )
          O = max(O, R.y/4.*min( min(V=U*mat2(1,-.58,0,1.15),V.y) , 1.-V-V.y ).x);    // draw equilateral triangle           
}




