const float pi = 3.14159265;

// http://dev.thi.ng/gradients/
//https://iquilezles.org/articles/palettes/
vec3 palette( float t ) {
    vec3 a = vec3(0.5,0.75 , 0.875);               
    vec3 b = vec3(0.5,0.25 , 0.125);              
    vec3 c = vec3(1.0,2.0  , 4.0);             
    vec3 d = vec3(0.0,-0.20, -0.4);             


    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;

    vec2 uv0 = uv;

    uv = vec2(uv.x*sin((length(uv)+iTime)*pi),uv.y*cos((length(uv)+iTime)*pi));

    float d = length(0.05/vec2(sin(abs(uv.x*4.0)+iTime),cos(abs(uv.y*4.0)+iTime)));

    vec3 col = vec3(d)*palette(iTime+length(uv0)-iTime*2.0);

    fragColor = vec4(col,1.0);
}
