void mainImage( out vec4 fragColor, in vec2 fragCoord) {
    vec4 leftCol = vec4(0.7098, 0.2078, 0.4078, 1.0);
    vec4 rightCol = vec4(0.1450, 0.1529, 0.4470 ,1.0);

    vec2 uv = (gl_FragCoord.xy - iResolution.xy * 0.5) / iResolution.xy;
    float frag = iResolution.x / iResolution.y;
    uv.x *= frag;

    float d = length(uv);
    float s = abs(sin(exp(5.0f - 4.0f * d) + iTime * 8.0f));
    fragColor = leftCol + s * (rightCol - leftCol);
}
