bool isIn(vec2 z){return length(z)<10.0;}
vec2 compMult(vec2 z1,vec2 z2){return vec2((z1.x*z2.x)-(z1.y*z2.y),(z1.x*z2.y)+(z2.x*z1.y));}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //variables
    float ITERATIONS = 300.0;
    float ZOOM = 1.0;
    float time = 10.2;
    vec2 JULIA_C = vec2(-0.42,-0.01);
    vec2 COORDS = vec2(0.0,0.0);

    //zoom in when clicked
    if (iMouse.z>0.0){
    ZOOM =10.0;
    COORDS=((2.0*iMouse.xy)-(iResolution.xy))/iResolution.y;
   }

    //get coordinate
    vec2 uv = COORDS+((4.0/ZOOM)*(fragCoord/iResolution.x))-(2.0*(iResolution.xy/iResolution.x)/ZOOM);

    //iterate equation a bunch
    vec2 z = uv;
    float i=0.0;
    for (i=0.0;i<ITERATIONS && isIn(z);++i){
    z = compMult(z,compMult(z,z)) + JULIA_C;
    }

    //calculate and output color
    vec3 col = vec3(0.0);
    if (!isIn(z)){
    i=i-log(log(length(z)))/log(3.0);
    i=i/5.0;
    col = abs(vec3(sin(i+time),sin(i+(time/3.0)),sin(i+(time/2.0))));
    }
    fragColor = vec4(col,1.0);
}
