void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 center = vec2(iMouse.x, iMouse.y)/iResolution.xy;
    vec2 off = uv - center;
    vec4 col = vec4(0.);
    for(int i= 0;i<32;i++)
    {
        float scale = 1.0 - gradicalscale*(float(i)/31.0);
         col += texture(iChannel0, off*scale+center);
    }
    fragColor = col/32.0;

}

