void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 resolution = iResolution.xy;

    float time = iTime;

    vec2 circle1 = vec2(0.5 + 0.3 * cos(time), 0.5 + 0.3 * sin(time)); 
    vec2 circle2 = vec2(0.5 + 0.2 * cos(time), 0.5 + 0.2 * sin(time)); 
    vec2 circle3 = vec2(0.5, 0.7);

    vec3 color1 = vec3(circle1.x, circle1.y, 1.); 
    vec3 color2 = vec3(circle2.y, circle2.x, 1.);
    vec3 color3 = vec3(0.1, 0.8, 0.2); 

    vec3 backgroundColor = 0.2 + 0.1 * cos(time + fragCoord.xxx *.1);

    float distance3 = length(fragCoord - resolution * circle3);
    float distance1 = length(fragCoord - resolution * circle1);  
    float distance2 = length(fragCoord - resolution * circle2);

    float circleRadius = .1 * resolution.x;

    vec3 finalColor = backgroundColor;

    if (distance1 < circleRadius) {
        finalColor = color1;
    }

    if (distance2 < circleRadius) {
        finalColor = color2;
    }

    if (distance3 < circleRadius) {
        finalColor = color3;
    }

    fragColor = vec4(finalColor, 4.0);
}
