void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 aspect=vec2(1,iResolution.y/iResolution.x);
    uv=abs(uv-vec2(0.5,0.5));
    float angle=atan(uv.y,uv.x)/(6.4);
    vec4 FFT=vec4(0,0,0,1);
         FFT =  texture( iChannel0,vec2(angle,min(1.0-length(uv),0.0)));
         FFT.r-=2.0*length(uv*vec2(2,1));
         if(length(uv*aspect)+0.01*sin(iTime*10.0+100.0*angle)+sin(iTime*15.0+100.0*angle)<0.02){
         FFT.r=smoothstep(0.0,FFT.r,.1);
         }

    vec4 col = vec4(0.8,0.1,72.0/255.0,0.0);
    col=mix(FFT,col,1.0-FFT.r-length(uv*3.0));
    fragColor = col;
}
