// from Dave Hoskins
// https://www.shadertoy.com/view/4djSRW
float h(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}
#define R iResolution.xy
void mainImage(out vec4 O,vec2 F)
{
    float s=.01;
    while (fract(h(floor(F*s)))>.5)
    {
        s+=s;
    }
    O=vec4(h(floor(F*s)));

}
