// golf by @fabrice
#define S(d, e) smoothstep(15. / r.y, 0., length(fract(u * 17. / 6.28 + d) - .5 ) -  e)

void mainImage(out vec4 o, vec2 u) {
    float t = iTime * .2;
    vec2 r = iResolution.xy;
    u -= r / 2.;

    u = vec2( 
              log( length(u) / r.y ) + cos(t) - t,
              atan(u.y, u.x) + t / 2.
            );

    o = S( .5,  .125)  
        + S( 0. , .33)  
        + o * 0.;
}

