#define red vec3(1.0, 0.0, 0.0)
#define blue vec3(0.0, 0.0, 1.0)
#define green vec3(0.0, 1.0, 0.0)
#define pi 3.14
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 rgb = vec3(0.0,0.0,0.0);

    float r1 = .5;
    float offset1 = pi/2.0;
    float offset2 = pi;
    float offset3 = 3.0*pi/2.0;
    float r2 = .04;


    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;

    if(uv.x * uv.x + uv.y * uv.y < r1){
        rgb = vec3(1.0, 1.0, 1.0);
    }
    vec2 path = vec2(cos(1.0*iTime),sin(1.0*iTime))/2.2;
    if((uv.x + path.x) * (uv.x + path.x) + (uv.y + path.y) * (uv.y + path.y) < r2){
        rgb = red;
    }
    path = vec2(cos(1.0*iTime+offset1),sin(1.0*iTime+offset1))/2.2;
    if((uv.x + path.x) * (uv.x + path.x) + (uv.y + path.y) * (uv.y + path.y) < r2){
        rgb = blue;
    }
    path = vec2(cos(1.0*iTime+offset2),sin(1.0*iTime+offset2))/2.2;
    if((uv.x + path.x) * (uv.x + path.x) + (uv.y + path.y) * (uv.y + path.y) < r2){
        rgb = green;
    }

    path = vec2(cos(1.0*iTime+offset2),sin(1.0*iTime+offset2))/2.2;
    if((uv.x + path.x) * (uv.x + path.x) + (uv.y + path.y) * (uv.y + path.y) < r2){
        rgb = green;
    }

    path = vec2(cos(1.0*iTime+offset3),sin(1.0*iTime+offset3))/2.2;
    if((uv.x + path.x) * (uv.x + path.x) + (uv.y + path.y) * (uv.y + path.y) < r2){
        rgb = vec3(0.0,0.0,0.0);;
    }
    // Output to screen
    fragColor = vec4(rgb, 1.0);
}
