vec4 duotone( in vec4 frag )
{
    vec4 color;
    float value = (frag.r + frag.g + frag.b) / 3.;
    if (value < 0.5) color = vec4(0,0,0,1);
    else color = vec4(1,1,1,1);
    return color;
}



vec4 grain( in vec4 frag )
{
    return vec4(frag.b, frag.b, frag.b, 1); // no idea why this doesn't work with "r" and "g", but anyways
}
