float box(vec2 p,vec2 b){p=abs(p)-b;return length(max(vec2(0.),p))+min(0.,max(p.x,p.y));}
vec3 erot(vec3 p,vec3 ax,float t){return mix(dot(ax,p)*ax,p,cos(t))+cross(ax,p)*sin(t);}
vec3 pcg3d(vec3 p){
    uvec3 q = floatBitsToUint(p)*1234567890u+1234567u;
    q.x+=q.y*q.z;q.y+=q.x*q.z;q.z+=q.y*q.x;
    q^=q>>16u;
    q.x+=q.y*q.z;q.y+=q.x*q.z;q.z+=q.y*q.x;
    return vec3(q)/float(-1U);    
}
vec3 pal(float t,vec3 c){return .5+.5*cos(6.28*(1.*t+c));}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy -.5* iResolution.xy)/iResolution.y;


    float bpm = iTime*125./60.+pcg3d(floor(uv.xyy*150.)).x*.1;
    float d = 100.;

    for(float i=0.;i++<8.;){
    vec3 rnd = pcg3d(floor(vec3(i,bpm,bpm)));

    vec2 uuv=uv;   
    uuv = erot(uuv.xyy,vec3(0.,0.,1.),rnd.x*6.28).xy;   
    uuv+=rnd.yz*rnd.x*.5;
    float dd = box(uuv,vec2(.7*exp(-pow(fract(-bpm),4.)),.0025+rnd.x*.02));
    d = min(d,dd*(1.-rnd.z*.5));
    }   
    d = smoothstep(.001,0.01,d);

    vec3 col = vec3(0.)+mix(pal(-floor(bpm)*.33,vec3(.0,.1,.2)),pal(floor(bpm)*.1,vec3(0.,.3,.7)),d);
    fragColor = vec4(sqrt(col),1.0);
}
