// Shadertoy-kompatible Version des Shaders

vec3 metalBlue(float t) {
    vec3 color = vec3(0.2 + 0.3 * sin(t * 6.28), 
                      0.3 + 0.3 * sin(t * 6.28 + 2.1), 
                      0.6 + 0.4 * sin(t * 6.28 + 4.2));
    return color;
}

float plasma(vec2 uv, float time) {
    float v = 0.0;
    vec2 c = uv * 2.0 - 1.0;
    v += sin((c.x + time) * 10.0);
    v += sin((c.y + time) * 10.0);
    v += sin((c.x + c.y + time) * 10.0);
    c += 1.0;
    v += sin(sqrt(c.x * c.x + c.y * c.y + 1.0) + time);
    v = v * 0.5;
    return v;
}

vec3 amigaPlasma(vec2 uv, float time) {
    float v = plasma(uv, time);
    return vec3(v * 0.5 + 0.5, v * 0.7 + 0.3, v + 0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    float aspect = iResolution.x / iResolution.y;

    float bendFactor = 0.2;
    float bend = sin(uv.y * 3.14159 + iTime) * bendFactor;
    uv.x += bend;

    float y = uv.y * iResolution.y;
    float t = iTime * 0.5;

    float cyclePosition = mod(y, 5.0);

    vec4 color;

    if (cyclePosition < 4.0) {
        float barIndex = floor(y / 5.0);
        float offset = mod(barIndex * 0.1 + t, 1.0);

        color = vec4(metalBlue(offset), 1.0);
    } else {
        color = vec4(0.0, 0.0, 0.0, 1.0);
    }

    vec2 center = vec2(0.5, 0.5);
    vec2 p = uv - center;
    p.x *= aspect;
    float ellipseSize = 0.3 + 0.1 * sin(iTime);
    if (length(p / ellipseSize) < 1.0) {
        vec3 plasmaColor = amigaPlasma(uv, iTime * 0.5);
        color = vec4(plasmaColor, 1.0);
    }

    fragColor = color;
}
