void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;

    // Carregando texturas
    vec3 video = texture(iChannel0, uv).rgb;
    vec3 london = texture(iChannel1, uv).rgb;

    // Definindo o chromakey e a tolerância de cor
    vec3 chromaKeyColor = vec3(0.05, 0.63, 0.14); //código rgb normalizado do verde
    float tolerance = 0.35;// ajustando a tolerância

    // cria uma máscara baseada na intensidade do verde
    float mask = step(tolerance, length(video - chromaKeyColor));

    // mistura o video e a imagem de fundo com base na máscara
    vec3 finalColor = mix(london, video, mask);

    // Resultado final 
    fragColor = vec4(finalColor, 1.0);
}
