#define A(U) texture(iChannel0,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U)
#define R iResolution.xy
vec4 opElongate( in vec3 p, in vec3 h )
{
    vec3 q = abs(p)-h;
    return vec4( max(q,0.0), min(max(q.x,max(q.y,q.z)),0.0) );
}
float sdCappedCylinder( vec3 p, vec2 h )
{
  vec2 d = abs(vec2(length(p.xy),p.z)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}
float roundBox(vec3 p, vec3 b , float r) {
    vec4 w = opElongate( p, .5*b );
    return w.w+sdCappedCylinder( w.xyz, vec2(r,.5*b.z) ) ;
}
float subtract (float a, float b) {
    return max(a,-b);
}
#define phi (.5*(1.+sqrt(5.)))
#define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define pi 3.14159265359
vec4 hash44(vec4 p4)
{
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

vec2 map (vec3 p) {
    float m = p.z;
    float h = 0.;
    float r = 1.;
    float d = 1e9;
    for (float i = 1.; i < 11.; i++) {
        float b = roundBox(p,vec3(vec2(phi,1)/r,.25*i),.01);
        if (b<0.) h = -.125*i;
        d = min(d,b);
        p.x += .5/r;
        p.xy *= ei(.5*pi);
        r *= phi;
    }
    d = subtract(m,d);
    //if (p.z>0.) d = p.z;
    return vec2(d,h);
}
Main {
    vec4 h = hash44(vec4(U,iTime,iFrame));

    Q = A(U);
    U = phi*(U-.5*R)/R.y;
    float a = pi*2.*h.x;
    float t = .5*pi*(1.-h.y*h.y);

    vec3 d = vec3(cos(t)*vec2(cos(a),sin(a)),sin(t));
    vec3 p = vec3(U,map(vec3(U,0)).y);
    p += .1*d;
    for(float i = 0.; i < 50.; i++) {
        vec2 u = p.xy;
        float m = map(p).x;
        p += d*m;
        if (m>1.) {
            Q +=3.*(1.-.5*d.y)*max(sin(-1.+atan(d.x,d.y)+vec4(1,2,3,4)),0.);
            break;
        }
    }
    //Q += sin(map(vec3(U.x,0,U.y)).x*100.+vec4(1,2,3,4));

}
