#define size 2.
#define d 1.1

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col1 = texture(iChannel0,vec2(uv.x-.001,uv.y)).xyz;
    vec3 col2 = texture(iChannel0,vec2(uv.x+.001,uv.y)).xyz;
    vec3 col = mix(col1,col2,0.5);
    if(mod(fragCoord.y/size+float(iFrame),2.)>=1.) col /= d;
    //if(mod(fragCoord.x/size/2.+float(iFrame),2.)>=1.) col /= d;

    fragColor = vec4(col,1.);
}
