void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float time = iTime * 0.2; 
    float n = mix(1.0, 200.0, (sin(time) + 1.0) * 0.5); 

    vec3 closestColor = vec3(0.0);
    float closestDistance = 1.0; 

    for(int i = 0; i < int(n); i++) {
        vec2 point = vec2(
            fract(sin(float(i) * 76.543) * 43758.5453),
            fract(cos(float(i) * 76.543) * 43758.5453)
        );
        vec3 color = vec3(
            fract(sin(float(i) * 43.1) * 547.881),
            fract(sin(float(i) * 91.2) * 651.317),
            fract(sin(float(i) * 12.3) * 435.733)
        );

        float dist = distance(uv, point);
        if(dist < closestDistance) {
            closestDistance = dist;
            closestColor = color;
        }
    }

    fragColor = vec4(closestColor, 1.0);
}
