void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    float ixy = iResolution.x / iResolution.y;
    vec3 c_fin = vec3(0.);

    float pix = 1. / iResolution.y;

    vec2 center = vec2(0.5*ixy, 0.5);

    float c = length(uv-center) - 0.25;

    // left top
    float cir = exp(-220. * abs(c));


    if(iMouse.x != 0.) {
        center.xy = (iMouse/iResolution.y).xy;
    }

    // left bottom
    if(uv.x < center.x && uv.y < center.y) {
        cir = step(0., abs(c)) - step(.01, abs(c));
    }

    // right top
    if(uv.x > center.x && uv.y > center.y) {
       //cir = smoothstep(0.01,0.,abs(c));
       cir = smoothstep(4.*pix,0.,abs(c));
       //cir = clamp(1. - (abs(c) - 2./iResolution.y)*iResolution.y, 0., 1.); 

    }

    // right bottom
    if(uv.x > center.x && uv.y < center.y) {
       cir = 1. - abs(c) / fwidth(c);
    }

    c_fin = mix(c_fin, vec3(1.), cir);


    float l = smoothstep(0.01,0.,abs(uv.x - center.x));
    //float l2 = smoothstep(0.01,0.,abs(uv.y - center.y)) * step(0., uv.x - center.x);
    float l2 = smoothstep(0.01,0.,abs(uv.y - center.y));
    //c_fin = mix(c_fin, vec3(1.,0.,0.), l+l2);
    c_fin += vec3(1.,0.,0.) * (l + l2);

    fragColor = vec4(c_fin,1.0);
}
