void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / min(iResolution.x, iResolution.y);

    float r = 0.3;
    vec3 color = vec3(1.0, 1.0, 0.);

    if (length(uv) < r) {
        color = vec3(1.0);
        float inner_r = 0.5 * r;
        float eye_sz = inner_r * 0.1;
        vec2 up_center = vec2(inner_r * sin(iTime), inner_r* cos(iTime));
        vec2 down_center = vec2(-up_center.x, -up_center.y);

        if (((uv.x * up_center.y - uv.y * up_center.x > 0.0 || distance(down_center, uv) < inner_r)) && 
            distance(uv, up_center) > inner_r && 
            distance(uv, down_center) > eye_sz || 
            distance(uv, up_center) < eye_sz
        ) {
            color = vec3(0.0);
        }
    }

    fragColor = vec4(color, 1.0);
}
