void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 emoji = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    float Face_Radius = 0.4;
    vec3 Face_Color = vec3(1.0, 1.0, 0.0);

    vec2 Face_Center = vec2(0.0, 0.0);

    float Face_Distance = length(emoji - Face_Center);
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);  // black background

    // Draw face
    if(Face_Distance < Face_Radius) {
        fragColor = vec4(Face_Color, 1.0);
    }

    // Eyes
    float Eye_Radius = 0.05;
    vec2 Left_Eye = vec2(-0.15, 0.1);
    vec2 Right_Eye = vec2(0.15, 0.1);
    float Left_Eye_Distance = length(emoji - Left_Eye);
    float Right_Eye_Distance = length(emoji - Right_Eye);

    // If within eye radius, set color to black for eyes
    if(Left_Eye_Distance < Eye_Radius || Right_Eye_Distance < Eye_Radius) {
        fragColor = vec4(0.0, 0.0, 0.0, 1.0); 
    }

    // Sad Face
    float Sad_Width = 0.2;
    float Sad_Height = 0.1;
    float Sad = -0.2;

    if(emoji.y > Sad && emoji.y < Sad + Sad_Height) {
        float Sad_Start_X = -Sad_Width / 2.0;
        float Sad_End_X = Sad_Width / 2.0;


        float Smile_Curve = -4.0 * Sad_Height * (emoji.x * emoji.x) / (Sad_Width * Sad_Width) + Sad_Height;


        if(emoji.x > Sad_Start_X && emoji.x < Sad_End_X && emoji.y < Sad + Smile_Curve) {
            fragColor = vec4(0.0, 0.0, 0.0, 1.0);  
        }
    }
}
