void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Размер изображения
    vec2 resolution = iResolution.xy;

    // Центр изображения
    vec2 center = resolution / 2.0;

    // Угол сектора
    float angle = 6.283185 / 6.0; // 6 секторов

    // Расстояние от центра до точки
    float dist = length(fragCoord - center);

    // Угол между точкой и центром
    float angleToPoint = atan(fragCoord.y - center.y, fragCoord.x - center.x);

    // Индекс сектора
    int sectorIndex = int((angleToPoint + 3.141592) / angle);

    // Переменная для хранения конечной точки
    vec2 finalPoint = fragCoord;

    // Меняем местами секторы 1 и 5
    if (sectorIndex == 1) {
        finalPoint = vec2(center.x + dist * cos(angle * 5.0), center.y + dist * sin(angle * 5.0));
    } else if (sectorIndex == 5) {
        finalPoint = vec2(center.x + dist * cos(angle), center.y + dist * sin(angle));
    }

    // Получаем цвет из исходного изображения и выводим его
    fragColor = texture(iChannel0, finalPoint / resolution);
}
