void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    int iterMax = 1000;



    float zoom = 1.3;


    float initialx = -2.;
    float initialy = -1.2;
    float initialstopy = 1.2;


    float xrel = -2. * iMouse.x / iResolution.x + 1.;
    float yrel = -2. * iMouse.y / iResolution.y + 1.;
    yrel = 0.;
    float xchangefactor = xrel;
    float ychangefactor = yrel;

    float timefac = (1.0 - 1.0 / pow(zoom, iTime)); // Adjust the exponent for sensitivity control


    float startx = initialx * (1. - timefac * (-xchangefactor + 1.)) ;
    float starty = initialy * (1. - timefac * (-ychangefactor + 1.));
    float stopy = initialstopy * (1. - timefac * (ychangefactor + 1.));



    float complexStep = (stopy - starty) / iResolution.y;

    float complexX = startx + fragCoord.x * complexStep;
    float complexY = starty + fragCoord.y * complexStep;


    float xn = 0.;
    float yn = 0.;

    int iteration = 0;

    for (; iteration < iterMax; iteration++){

        float nextxn = xn * xn - yn * yn + complexX;
        float nextyn = (2. * xn * yn) + complexY;

        xn = nextxn;
        yn = nextyn;



        if (xn*xn + yn*yn > 4.) {
            break;
        };
    };


    float r = 0.;
    float g = 0.;
    float b = 0.;

    if (iteration < iterMax) {

        r = float(iteration * 20 % 255) / 255.;
        g = float(iteration * 40 % 255) / 255.;
        b = float(iteration * 60 % 255) / 255.; 

    }

    // Output to screen
    fragColor = vec4(r, g, b, 1.0);
}
