void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord / iResolution.y;

    vec4 blue = vec4(0.356, 0.807, 0.98, 1.0);
    vec4 pink = vec4(0.96 ,0.662, 0.721, 1.0);

    // Output to screen
    fragColor = vec4(1.0);

    if( uv.y < 0.4) {
        fragColor = pink;
    }
    if( uv.y < 0.2) {
        fragColor = blue;
    }


    if( uv.y > 0.6) {
        fragColor = pink;
    }
    if( uv.y > 0.8) {
        fragColor = blue;
    }

}
