void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float n_scl_min = min(iResolution.x, iResolution.y);
    vec2 o_trn = (fragCoord.xy-iResolution.xy*.5)/n_scl_min;
    o_trn*= 5.;
    vec2 o2 = abs(fract(o_trn)-.5);
    float n = o2.x*o2.y;
    float n_diff = 0.03;
    n = smoothstep(0.01, 0.0025, n);
    fragColor = vec4(
        n
    );
}
