// Built from tutorial by The Art Of Code: https://youtu.be/2R7h76GoIJM?si=noFB-47cRYlyPvF6

float Hash21(vec2 p){
    p = fract(p*vec2(237.21,656.56));
    p += dot(p, p+32.23);
    return fract(p.x*p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 UV = fragCoord/iResolution.xy;
    vec3 col = vec3(0);

    uv += iTime * .2;
    uv *= 5.;
    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);

    float n = Hash21(id);
    float width = .3*sin(UV.y*3.1)*sin(UV.x*3.1);

    if(n<.5) gv.x *= -1.;
    vec2 cUv = gv-.5*sign(gv.x + gv.y+.001);
    float d = abs(abs(gv.x + gv.y)-.5);
    d = length(cUv);

    float mask = smoothstep(.01, -.01, abs(d-.5)-width);

    float angle = atan(cUv.x, cUv.y);
    float checker = mod(id.x+id.y, 2.)*2.-1.;

    float x = fract(checker*angle*5./1.57-iTime);
    float y = (d-(.5-width))/(2.*width);
    y = abs(y - .5)*2.;

    // messing with numbers here gives lots of cool patterns
    vec2 tUv = vec2(x, y); // try setting y to 0 or multiplying it with different numbers.
    float pattern = .4/length(tUv-.5);//can experiment with thickness and brightness of the pattern.
    col.rgb -= tan(vec3(tUv,0)*mask+2.)+pattern; //can experiment with colors

    fragColor = vec4(col,1.0);
}
