// Inspired by "Cyclotron" by ChunderFPV
// Custom variant with minimal changes

#define A(a) mat2(cos((a)*6.2832), -sin((a)*6.2832), sin((a)*6.2832), cos((a)*6.2832))
#define H(a) (cos(radians(vec3(0, 60, 120)) + (a) * 6.2832) * 0.5 + 0.5)
#define R iResolution.xy

float cube(vec3 p, mat2 h, mat2 v)
{
    float a, b;

    p.yz *= h;
    p.xz *= v;
    p = abs(p);
    a = max(p.x, max(p.y, p.z)) - 2.414; // Outer cube boundary

    p = abs(p - round(p));
    b = max(p.x, max(p.y, p.z)) - 0.3; // Inner cube boundary

    return max(a, b);
}

void mainImage(out vec4 C, vec2 U)
{
    float t = iTime / 60.0, s = 1.0, d = 0.0, i = 0.0, r, r2;

    vec2 m = vec2((R.y + sin(iTime * 0.1)) / R.y, 1.0);
    vec3 o = vec3(0.0, -6.0, -40.0 / (m.y + 1.0));
    vec3 u = normalize(vec3(U - 0.5 * R, R.y * sin(3.141592 * t * pow(t, 0.55))));
    vec3 c = vec3(0.1), p;

    mat2 h = A(m.x), v = A(m.y / 30.0), ch = A(cos(iTime / 2.0) * 0.1), cv = A(sin(-iTime / 2.0) * 0.5);

    for (; i++ < 90.0;)
    {
        p = o + u * d;
        p.yz *= v;
        p.xz *= h;
        r = length(p.xz);
        r2 = length(p);

        s = cube(p, ch, cv);
        s = min(s, max(length(p) - 5.5, 5.4 - length(p.xy)));
        p.xz = vec2(atan(p.x, p.z) / 6.2832, r);
        p.x -= round(p.z) * t * sign(p.y);
        p.xz = abs(p.xz - round(p.xz));
        p.y = abs(p.y) - 15.0 + 3.0 * sin(iTime * 0.1);
        s = min(s, max(abs(p.y) - 12.0, max(p.x, p.z) - min(1.0, 0.5 / r)));

        if (s < 0.001 || d > 1000.0) break;
        d += s * 0.5;
        c += min(vec3(s), 0.003 / s * (H(s + 5.0 / r2 - 0.1) * 0.6 + 0.1));
    }

    C = vec4(c * c, 1.0);
}
